// frontend/src/App.js
import React, { useState, useRef } from "react";

/*
  IMPORTANT:
  - When running locally this expects the backend at http://localhost:8000
  - When you deploy the backend, set BACKEND_URL to the deployed URL
*/
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

const LANG_OPTIONS = [
  { code: "en", label: "English" },
  { code: "es", label: "Spanish" },
  { code: "ur", label: "Urdu" },
  { code: "fr", label: "French" },
  { code: "da", label: "danish" },
  { code: "hi", label: "Hindi" },
  { code: "ar", label: "Arabic" },
  { code: "zh", label: "Chinese (zh)" },
];

export default function App() {
  const [listening, setListening] = useState(false);
  const [original, setOriginal] = useState("");
  const [translated, setTranslated] = useState("");
  const [srcLang, setSrcLang] = useState("en");
  const [tgtLang, setTgtLang] = useState("es");
  const recognitionRef = useRef(null);

  function speakText(text, langCode) {
    if (!("speechSynthesis" in window)) return;
    const u = new SpeechSynthesisUtterance(text);
    // some browsers expect full locale (try common ones)
    u.lang = langCode === "zh" ? "zh-CN" : (langCode === "ur" ? "ur-PK" : `${langCode}-${langCode.toUpperCase()}`);
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(u);
  }

  const startListening = () => {
    // Support webkit prefix
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Web Speech API not supported in this browser. Use Chrome/Edge.");
      return;
    }
    const recog = new SpeechRecognition();
    recog.lang = srcLang;
    recog.interimResults = true;
    recog.continuous = true;

    recog.onresult = async (ev) => {
      // build final text from results
      let interim = "";
      let final = "";
      for (let i = ev.resultIndex; i < ev.results.length; i++) {
        const r = ev.results[i];
        if (r.isFinal) final += r[0].transcript + " ";
        else interim += r[0].transcript;
      }
      const currentText = (original ? original + " " : "") + final + (interim ? interim : "");
      setOriginal(currentText.trim());

      // send translation for final text only to reduce calls
      if (final.trim().length > 0) {
        try {
          const res = await fetch(`${BACKEND_URL}/translate`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ text: currentText.trim(), src_lang: srcLang, tgt_lang: tgtLang }),
          });
          const j = await res.json();
          if (j.translated_text) {
            setTranslated(j.translated_text);
          } else if (j.error) {
            console.warn("Translate error:", j.error);
          }
        } catch (err) {
          console.error("Translate request failed:", err);
        }
      }
    };

    recog.onerror = (e) => {
      console.error("Speech recognition error", e);
    };

    recog.onend = () => {
      setListening(false);
    };

    recognitionRef.current = recog;
    recog.start();
    setListening(true);
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      recognitionRef.current = null;
      setListening(false);
    }
  };

  return (
    <div style={{ maxWidth: 720, margin: "24px auto", padding: 16, fontFamily: "Inter, Arial, sans-serif" }}>
      <h1>Healthcare Translation (Free)</h1>
      <div style={{ display: "flex", gap: 8, marginBottom: 12, flexWrap: "wrap" }}>
        <div>
          <label style={{ display: "block", fontSize: 12 }}>From</label>
          <select value={srcLang} onChange={(e) => setSrcLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => <option key={o.code} value={o.code}>{o.label}</option>)}
          </select>
        </div>
        <div>
          <label style={{ display: "block", fontSize: 12 }}>To</label>
          <select value={tgtLang} onChange={(e) => setTgtLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => <option key={o.code} value={o.code}>{o.label}</option>)}
          </select>
        </div>

        {!listening ? (
          <button onClick={startListening} style={{ padding: "8px 12px" }}>üé§ Start</button>
        ) : (
          <button onClick={stopListening} style={{ padding: "8px 12px" }}>‚èπ Stop</button>
        )}

        <button onClick={() => speakText(translated, tgtLang)} disabled={!translated} style={{ padding: "8px 12px" }}>
          üîä Speak Translation
        </button>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3 style={{ margin: "0 0 8px 0" }}>Original ({srcLang})</h3>
          <textarea value={original} readOnly style={{ width: "100%", minHeight: 160 }} />
        </div>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3 style={{ margin: "0 0 8px 0" }}>Translated ({tgtLang})</h3>
          <textarea value={translated} readOnly style={{ width: "100%", minHeight: 160 }} />
        </div>
      </div>

      <p style={{ marginTop: 12, color: "#666", fontSize: 13 }}>
        Note: This uses your browser's speech-to-text and a free Hugging Face translation model proxy.
      </p>
    </div>
  );
}
