import React, { useState } from "react";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

// Expanded language list (match backend SUPPORTED_LANGS)
const LANG_OPTIONS = [
  { code: "en", label: "English" },
  { code: "es", label: "Spanish" },
  { code: "ru", label: "Russian" },
  { code: "zh", label: "Chinese" },
  { code: "fr", label: "French" },
  { code: "de", label: "German" },
  { code: "it", label: "Italian" },
  { code: "ar", label: "Arabic" },
  { code: "hi", label: "Hindi" },
  { code: "ja", label: "Japanese" },
  { code: "ko", label: "Korean" },
  { code: "ur", label: "Urdu" }
];

export default function App() {
  const [original, setOriginal] = useState("");
  const [translated, setTranslated] = useState("");
  const [srcLang, setSrcLang] = useState("en");
  const [tgtLang, setTgtLang] = useState("es");
  const [loading, setLoading] = useState(false);

  const handleTranslate = async () => {
    if (!original.trim()) return;

    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/translate`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: original, src_lang: srcLang, tgt_lang: tgtLang }),
      });

      const data = await res.json();
      if (data.translated_text) {
        setTranslated(data.translated_text);
      } else if (data.error) {
        alert("Translate error: " + data.error);
      }
    } catch (err) {
      console.error("Translate request failed:", err);
      alert("Translate request failed. Check backend.");
    } finally {
      setLoading(false);
    }
  };

  const speakText = (text, lang) => {
    if (!("speechSynthesis" in window)) return;
    const u = new SpeechSynthesisUtterance(text);

    // Mapping for speech synthesis voices
    const langMap = {
      ur: "ur-PK",
      es: "es-ES",
      fr: "fr-FR",
      de: "de-DE",
      it: "it-IT",
      ar: "ar-SA",
      hi: "hi-IN",
      ja: "ja-JP",
      ko: "ko-KR",
      zh: "zh-CN",
      ru: "ru-RU",
      en: "en-US"
    };

    u.lang = langMap[lang] || "en-US";
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(u);
  };

  return (
    <div style={{ maxWidth: 720, margin: "24px auto", padding: 16, fontFamily: "Inter, Arial, sans-serif" }}>
      <h1>Healthcare Translation AI</h1>

      {/* Language Select */}
      <div style={{ display: "flex", gap: 8, marginBottom: 12, flexWrap: "wrap" }}>
        <div>
          <label style={{ display: "block", fontSize: 12 }}>From</label>
          <select value={srcLang} onChange={(e) => setSrcLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => (
              <option key={o.code} value={o.code}>
                {o.label}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label style={{ display: "block", fontSize: 12 }}>To</label>
          <select value={tgtLang} onChange={(e) => setTgtLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => (
              <option key={o.code} value={o.code}>
                {o.label}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Translation Areas */}
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3>Original ({srcLang})</h3>
          <textarea
            value={original}
            onChange={(e) => setOriginal(e.target.value)}
            style={{ width: "100%", minHeight: 160 }}
          />
          <button onClick={handleTranslate} disabled={loading} style={{ marginTop: 8, padding: "8px 12px" }}>
            {loading ? "Translating..." : "Translate"}
          </button>
        </div>

        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3>Translated ({tgtLang})</h3>
          <textarea value={translated} readOnly style={{ width: "100%", minHeight: 160 }} />
          <button
            onClick={() => speakText(translated, tgtLang)}
            disabled={!translated}
            style={{ marginTop: 8, padding: "8px 12px" }}
          >
            🔊 Speak Translation
          </button>
        </div>
      </div>

      <p style={{ marginTop: 12, color: "#666", fontSize: 13 }}>
        This app uses Helsinki-NLP models for translation and pivots through English if direct translation is not available.
      </p>
    </div>
  );
}
