import React, { useState, useRef } from "react";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

const LANG_OPTIONS = [
  { code: "es", label: "Spanish" },
  { code: "ur", label: "Urdu" },
  { code: "fr", label: "French" },
  { code: "de", label: "German" }
];

export default function App() {
  const [recording, setRecording] = useState(false);
  const [original, setOriginal] = useState("");
  const [translated, setTranslated] = useState("");
  const [audioURL, setAudioURL] = useState("");
  const [tgtLang, setTgtLang] = useState("es");
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      chunksRef.current = [];
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(chunksRef.current, { type: "audio/wav" });
        const formData = new FormData();
        formData.append("file", audioBlob, "recording.wav");
        formData.append("target_lang", tgtLang);

        try {
          const res = await fetch(`${BACKEND_URL}/process-audio`, {
            method: "POST",
            body: formData
          });
          const data = await res.json();
          if (data.error) {
            console.error("Error:", data.error);
          } else {
            setOriginal(data.original_text);
            setTranslated(data.translated_text);
            setAudioURL(BACKEND_URL + data.audio_url);
          }
        } catch (err) {
          console.error("Upload failed:", err);
        }
      };

      mediaRecorder.start();
      setRecording(true);
    } catch (err) {
      console.error("Mic access denied:", err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  return (
    <div style={{ maxWidth: 720, margin: "24px auto", padding: 16 }}>
      <h1>Healthcare Translation (Audio)</h1>

      <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
        <div>
          <label>Target Language:</label>
          <select value={tgtLang} onChange={(e) => setTgtLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => (
              <option key={o.code} value={o.code}>
                {o.label}
              </option>
            ))}
          </select>
        </div>

        {!recording ? (
          <button onClick={startRecording}>üé§ Start Recording</button>
        ) : (
          <button onClick={stopRecording}>‚èπ Stop</button>
        )}
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3>Original</h3>
          <textarea value={original} readOnly style={{ width: "100%", minHeight: 160 }} />
        </div>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3>Translated</h3>
          <textarea value={translated} readOnly style={{ width: "100%", minHeight: 160 }} />
        </div>
      </div>

      {audioURL && (
        <div style={{ marginTop: 12 }}>
          <h3>Translated Audio</h3>
          <audio controls src={audioURL} />
        </div>
      )}
    </div>
  );
}
