// src/App.js
import React, { useState } from "react";
import axios from "axios";

const SUPPORTED_LANGS = [
  { code: "en", label: "English" },
  { code: "es", label: "Spanish" },
  { code: "de", label: "German" },
  { code: "zh", label: "Chinese" },
  { code: "fr", label: "French" },
  { code: "ja", label: "Japanese" },
  { code: "ko", label: "Korean" },
  { code: "ar", label: "Arabic" },
  { code: "ru", label: "Russian" },
  { code: "it", label: "Italian" },
  { code: "nl", label: "Dutch" },
  { code: "sv", label: "Swedish" },
  { code: "pl", label: "Polish" },
  { code: "fi", label: "Finnish" },
  { code: "no", label: "Norwegian" },
  { code: "cs", label: "Czech" },
  { code: "el", label: "Greek" },
  { code: "tr", label: "Turkish" },
  { code: "da", label: "Danish" }
];

function App() {
  const [text, setText] = useState("");
  const [srcLang, setSrcLang] = useState("en");
  const [tgtLang, setTgtLang] = useState("es");
  const [translatedText, setTranslatedText] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleTranslate = async () => {
    setLoading(true);
    setError("");
    setTranslatedText("");

    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        src_lang: srcLang,
        tgt_lang: tgtLang
      });

      if (response.data.translated_text) {
        setTranslatedText(response.data.translated_text);
      } else if (response.data.error) {
        setError(response.data.error);
      }
    } catch (err) {
      setError(err.message || "Translation failed");
    }

    setLoading(false);
  };

  return (
    <div style={{ maxWidth: "600px", margin: "2rem auto", fontFamily: "Arial" }}>
      <h2>Healthcare Translation Tool</h2>

      <div style={{ marginBottom: "1rem" }}>
        <textarea
          rows="4"
          cols="50"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text to translate"
        />
      </div>

      <div style={{ marginBottom: "1rem" }}>
        <label>Source Language: </label>
        <select value={srcLang} onChange={(e) => setSrcLang(e.target.value)}>
          {SUPPORTED_LANGS.map((lang) => (
            <option key={lang.code} value={lang.code}>{lang.label}</option>
          ))}
        </select>

        <label style={{ marginLeft: "1rem" }}>Target Language: </label>
        <select value={tgtLang} onChange={(e) => setTgtLang(e.target.value)}>
          {SUPPORTED_LANGS.map((lang) => (
            <option key={lang.code} value={lang.code}>{lang.label}</option>
          ))}
        </select>
      </div>

      <button onClick={handleTranslate} disabled={loading || !text}>
        {loading ? "Translating..." : "Translate"}
      </button>

      {translatedText && (
        <div style={{ marginTop: "1rem", padding: "1rem", border: "1px solid #ccc", backgroundColor: "#f5f5f5" }}>
          <strong>Translated Text:</strong>
          <p>{translatedText}</p>
        </div>
      )}

      {error && (
        <div style={{ marginTop: "1rem", color: "red" }}>
          <strong>Error:</strong> {error}
        </div>
      )}
    </div>
  );
}

export default App;
