// frontend/src/App.js
import React, { useState } from "react";

/*
  IMPORTANT:
  - When running locally, the backend is expected at http://localhost:8000
  - When deployed, set REACT_APP_BACKEND_URL to your deployed backend URL
*/
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

const LANG_OPTIONS = [
  { code: "en", label: "English" },
  { code: "es", label: "Spanish" },
  { code: "fr", label: "French" },
  { code: "ur", label: "Urdu" },
];

export default function App() {
  const [original, setOriginal] = useState("");
  const [translated, setTranslated] = useState("");
  const [srcLang, setSrcLang] = useState("en");
  const [tgtLang, setTgtLang] = useState("es");
  const [loading, setLoading] = useState(false);

  const handleTranslate = async () => {
    if (!original.trim()) return;

    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/translate`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: original, src_lang: srcLang, tgt_lang: tgtLang }),
      });
      const data = await res.json();
      if (data.translated_text) setTranslated(data.translated_text);
      else if (data.error) alert("Translate error: " + data.error);
    } catch (err) {
      console.error(err);
      alert("Translation request failed");
    }
    setLoading(false);
  };

  const speakText = (text, lang) => {
    if (!("speechSynthesis" in window)) return;
    const utter = new SpeechSynthesisUtterance(text);
    utter.lang = lang === "ur" ? "ur-PK" : lang === "fr" ? "fr-FR" : lang === "es" ? "es-ES" : "en-US";
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(utter);
  };

  return (
    <div style={{ maxWidth: 720, margin: "24px auto", padding: 16, fontFamily: "Inter, Arial, sans-serif" }}>
      <h1>Healthcare Translation (Free)</h1>

      <div style={{ display: "flex", gap: 8, marginBottom: 12, flexWrap: "wrap" }}>
        <div>
          <label style={{ display: "block", fontSize: 12 }}>From</label>
          <select value={srcLang} onChange={(e) => setSrcLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => <option key={o.code} value={o.code}>{o.label}</option>)}
          </select>
        </div>

        <div>
          <label style={{ display: "block", fontSize: 12 }}>To</label>
          <select value={tgtLang} onChange={(e) => setTgtLang(e.target.value)}>
            {LANG_OPTIONS.map((o) => <option key={o.code} value={o.code}>{o.label}</option>)}
          </select>
        </div>

        <button onClick={handleTranslate} disabled={loading} style={{ padding: "8px 12px" }}>
          {loading ? "Translating..." : "Translate"}
        </button>

        <button onClick={() => speakText(translated, tgtLang)} disabled={!translated} style={{ padding: "8px 12px" }}>
          ðŸ”Š Speak Translation
        </button>
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3 style={{ margin: "0 0 8px 0" }}>Original ({srcLang})</h3>
          <textarea
            value={original}
            onChange={(e) => setOriginal(e.target.value)}
            style={{ width: "100%", minHeight: 160 }}
          />
        </div>

        <div style={{ background: "#fff", padding: 12, borderRadius: 8 }}>
          <h3 style={{ margin: "0 0 8px 0" }}>Translated ({tgtLang})</h3>
          <textarea value={translated} readOnly style={{ width: "100%", minHeight: 160 }} />
        </div>
      </div>

      <p style={{ marginTop: 12, color: "#666", fontSize: 13 }}>
        Note: Translation powered by mT5-small model. Audio playback uses your browserâ€™s speech synthesis.
      </p>
    </div>
  );
}
